    private void new_strikeMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_new_strikeMouseClicked
            try {
   
            strikedialog.getContentPane();         
            strikedialog.setSize(400, 350);
        
            strikedialog.pack();
            strikedialog.setLocationRelativeTo(drag_panel); 
        strikedialog.setVisible(true);
    } catch (Exception ex) {
        ex.printStackTrace();
    }        // TODO add your handling code here:
    }//GEN-LAST:event_new_strikeMouseClicked

